// Create Signature Hash
var signature = crypto.createHmac('sha256', secret).update(base64UrlHeader+"."+base64UrlBody).digest('hex')

// Encode Signature to Base64Url String
var base64UrlSignature = Buffer.from(signature).toString('base64');

